/*-->source: http://postgis.net/workshops/postgis-intro/history_tracking.html--DROP TABLE sheat_splice_history;--Create history table. This is the table we will use to store all the historical edit information. In addition to all the fields from sheat_splice, we add five more fields:*/DROP TABLE IF EXISTS sheat_splice_history;CREATE TABLE IF NOT EXISTS sheat_splice_history ( created timestamp without time zone, created_by character varying(64), deleted timestamp without time zone, deleted_by character varying(64), like sheat_splice );GRANT SELECT, UPDATE, INSERT, TRIGGER ON TABLE sheat_splice_history TO operatore_r;--we import the current state of the active table into the history table, so we have a starting point to trace history from. Note that we fill in the creation time and creation user, but leave the deletion records NULL, but in case it exist already, I truncate it:TRUNCATE sheat_splice_history;INSERT INTO sheat_splice_history SELECT now(), current_user, null, null, * FROM sheat_splice;--Now we need three triggers on the active table, for INSERT, DELETE and UPDATE actions. First we create the trigger functions, then bind them to the table as triggers.--For an insert, we just add a new record into the history table with the creation time/user:CREATE OR REPLACE FUNCTION sheat_splice_insert() RETURNS trigger AS$$  BEGIN    INSERT INTO schemaDB.sheat_splice_history VALUES (current_timestamp, current_user, null, null, NEW.*);    RETURN NEW;  END;$$LANGUAGE plpgsql;CREATE TRIGGER sheat_splice_insert_trigger AFTER INSERT ON sheat_splice FOR EACH ROW EXECUTE PROCEDURE sheat_splice_insert();--For a deletion, we just mark the currently active history record (the one with a NULL deletion time) as deleted:CREATE OR REPLACE FUNCTION sheat_splice_delete() RETURNS trigger AS$$  BEGIN    UPDATE schemaDB.sheat_splice_history      SET deleted = current_timestamp, deleted_by = current_user      WHERE deleted IS NULL and gidd = OLD.gidd;    RETURN NULL;  END;$$LANGUAGE plpgsql;CREATE TRIGGER sheat_splice_delete_trigger AFTER DELETE ON sheat_splice FOR EACH ROW EXECUTE PROCEDURE sheat_splice_delete();--For an update, we first mark the active history record as deleted, then insert a new record for the updated state:CREATE OR REPLACE FUNCTION sheat_splice_update() RETURNS trigger AS$$  BEGIN    UPDATE schemaDB.sheat_splice_history      SET deleted = current_timestamp, deleted_by = current_user      WHERE deleted IS NULL and gidd = OLD.gidd;    INSERT INTO schemaDB.sheat_splice_history    VALUES      (current_timestamp, current_user, null, null, NEW.*);    RETURN NEW;  END;$$LANGUAGE plpgsql;CREATE TRIGGER sheat_splice_update_trigger AFTER UPDATE ON sheat_splice FOR EACH ROW EXECUTE PROCEDURE sheat_splice_update();